import { useForm, useFieldArray } from 'react-hook-form';

export default function CreateForm() {
  const { register, control, handleSubmit, watch } = useForm({
    defaultValues: {
      fields: [{ label: '', type: 'text' }],
    },
  });

  const { fields, append, remove } = useFieldArray({
    control,
    name: 'fields',
  });

  const onSubmit = (data) => {
    // Adataid feldolgozása vagy küldése
    console.log('Űrlap adatok:', data);
  };

  return (
    <div>
      <h1>Saját Űrlap Készítése</h1>
      <form onSubmit={handleSubmit(onSubmit)}>
        {fields.map((field, index) => (
          <div key={field.id}>
            <label>
              Mező {index + 1} - Cím:
              <input
                type="text"
                {...register(`fields.${index}.label`)}
                defaultValue={field.label}
              />
            </label>
            <label>
              Mező {index + 1} - Típus:
              <select
                {...register(`fields.${index}.type`)}
                defaultValue={field.type}
              >
                <option value="text">Szöveg</option>
                <option value="number">Szám</option>
                {/* További típusokat itt adhatsz hozzá */}
              </select>
            </label>
            <button type="button" onClick={() => remove(index)}>
              Törlés
            </button>
          </div>
        ))}
        <button
          type="button"
          onClick={() => append({ label: '', type: 'text' })}
        >
          Mező Hozzáadása
        </button>
        <br />
        <button type="submit">Űrlap Elküldése</button>
      </form>
    </div>
  );
}
